mapper.select:

select * from t_sjlianjie
WHERE 1=1


		AND sjlianjieName LIKE '%${sjlianjie.sjlianjieName}%'


		AND sjlianjiePhone LIKE '%${sjlianjie.sjlianjiePhone}%'


		AND sjlianjieId = #{sjlianjie.sjlianjieId}


		AND sjlianjieType = #{sjlianjie.sjlianjieType}


		AND sjlianjieType1 = #{sjlianjie.sjlianjieType1}


order by sjlianjieId

	limit #{page},#{rows}
