mapper.select:

select * from t_sjxiaoxi
WHERE 1=1


		AND sjxiaoxiName LIKE '%${sjxiaoxi.sjxiaoxiName}%'


		AND sjxiaoxiPhone LIKE '%${sjxiaoxi.sjxiaoxiPhone}%'


		AND sjxiaoxiId = #{sjxiaoxi.sjxiaoxiId}


		AND sjxiaoxiType = #{sjxiaoxi.sjxiaoxiType}


		AND sjxiaoxiType1 = #{sjxiaoxi.sjxiaoxiType1}


order by sjxiaoxiId

	limit #{page},#{rows}
