mapper.select:

select * from t_yxinxi
WHERE 1=1

	 AND yxinxiDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND yxinxiDate <= #{edate,jdbcType=TIMESTAMP} 



		 AND yxinxiName LIKE  '%${yxinxi.yxinxiName}%' 


		 AND yxinxiId = #{yxinxi.yxinxiId} 


		 AND yxtypeId = #{yxinxi.yxtypeId} 


		 AND yxinxiType = #{yxinxi.yxinxiType} 


		 AND yxinxiType1 = #{yxinxi.yxinxiType1} 


		 AND yonghuId = #{yxinxi.yonghuId} 


		 AND byumenId = #{yxinxi.byumenId} 


		 AND yhroleId = #{yxinxi.yhroleId} 


		 AND byuyuanId = #{yxinxi.byuyuanId} 


		 AND byuzhiId = #{yxinxi.byuzhiId} 


		 AND userId = #{yxinxi.userId} 


		 AND bumenId = #{yxinxi.bumenId} 


		 AND buyuanId = #{yxinxi.buyuanId} 


		 AND buzhiId = #{yxinxi.buzhiId} 


		 AND roleId = #{yxinxi.roleId} 


order by yxinxiId 

	limit #{page},#{rows}
