mapper.select:

select * from t_yyijian
WHERE 1=1

	 AND yyijianDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND yyijianDate <= #{edate,jdbcType=TIMESTAMP} 



		 AND yyijianName LIKE  '%${yyijian.yyijianName}%' 


		 AND yyijianId = #{yyijian.yyijianId} 


		 AND yxtypeId = #{yyijian.yxtypeId} 


		 AND yyijianType = #{yyijian.yyijianType} 


		 AND yyijianType1 = #{yyijian.yyijianType1} 


		 AND yonghuId = #{yyijian.yonghuId} 


		 AND byumenId = #{yyijian.byumenId} 


		 AND yhroleId = #{yyijian.yhroleId} 


		 AND byuyuanId = #{yyijian.byuyuanId} 


		 AND byuzhiId = #{yyijian.byuzhiId} 


		 AND userId = #{yyijian.userId} 


		 AND bumenId = #{yyijian.bumenId} 


		 AND buyuanId = #{yyijian.buyuanId} 


		 AND buzhiId = #{yyijian.buzhiId} 


		 AND roleId = #{yyijian.roleId} 


order by yyijianId 

	limit #{page},#{rows}
